     1→"use client";
     2→
     3→import { useState, useEffect } from "react";
     4→import { useForm } from "react-hook-form";
     5→import { zodResolver } from "@hookform/resolvers/zod";
     6→import * as z from "zod";
     7→import { motion } from "framer-motion";
     8→import { Check, Loader2 } from "lucide-react";
     9→import { Button } from "@/components/ui/button";
    10→import {
    11→  Form,
    12→  FormControl,
    13→  FormDescription,
    14→  FormField,
    15→  FormItem,
    16→  FormLabel,
    17→  FormMessage,
    18→} from "@/components/ui/form";
    19→import { Input } from "@/components/ui/input";
    20→import { Checkbox } from "@/components/ui/checkbox";
    21→
    22→const formSchema = z.object({
    23→  email: z.string().email({
    24→    message: "Please enter a valid email address.",
    25→  }),
    26→  name: z.string().optional(),
    27→  agreeToPrivacy: z.boolean().refine((val) => val === true, {
    28→    message: "You must agree to the privacy policy to join the waitlist.",
    29→  }),
    30→});
    31→
    32→type FormData = z.infer<typeof formSchema>;
    33→
    34→export function WaitlistForm() {
    35→  const [isSubmitting, setIsSubmitting] = useState(false);
    36→  const [isSuccess, setIsSuccess] = useState(false);
    37→  const [count, setCount] = useState<number>(0);
    38→
    39→  const form = useForm<FormData>({
    40→    resolver: zodResolver(formSchema),
    41→    defaultValues: {
    42→      email: "",
    43→      name: "",
    44→      agreeToPrivacy: false,
    45→    },
    46→  });
    47→
    48→  // Fetch initial count on mount
    49→  useEffect(() => {
    50→    fetch('/api/waitlist')
    51→      .then(res => res.json())
    52→      .then(data => setCount(data.count))
    53→      .catch(() => {});
    54→  }, []);
    55→
    56→  async function onSubmit(values: FormData) {
    57→    setIsSubmitting(true);
    58→
    59→    try {
    60→      const response = await fetch('/api/waitlist', {
    61→        method: 'POST',
    62→        headers: { 'Content-Type': 'application/json' },
    63→        body: JSON.stringify({
    64→          email: values.email,
    65→          name: values.name || undefined,
    66→        }),
    67→      });
    68→
    69→      const data = await response.json();
    70→
    71→      if (!response.ok) {
    72→        throw new Error(data.error || 'Failed to join waitlist');
    73→      }
    74→
    75→      setCount(data.count); // Update counter
    76→      setIsSuccess(true);
    77→
    78→      // Reset form after 3 seconds
    79→      setTimeout(() => {
    80→        form.reset();
    81→        setIsSuccess(false);
    82→      }, 3000);
    83→    } catch (error) {
    84→      console.error("Waitlist submission error:", error);
    85→      form.setError("root", {
    86→        message: error instanceof Error ? error.message : "Something went wrong. Please try again.",
    87→      });
    88→    } finally {
    89→      setIsSubmitting(false);
    90→    }
    91→  }
    92→
    93→  if (isSuccess) {
    94→    return (
    95→      <motion.div
    96→        initial={{ opacity: 0, scale: 0.95 }}
    97→        animate={{ opacity: 1, scale: 1 }}
    98→        className="flex flex-col items-center gap-4 p-8 rounded-2xl bg-[hsl(var(--brand-primary)/0.1)] border border-[hsl(var(--brand-primary)/0.2)]"
    99→      >
   100→        <div className="w-16 h-16 rounded-full bg-[hsl(var(--brand-primary))] flex items-center justify-center">
   101→          <Check className="w-8 h-8 text-white" />
   102→        </div>
   103→        <h3 className="text-2xl font-bold text-white font-[family-name:var(--font-manrope)]">
   104→          You're on the list!
   105→        </h3>
   106→        <p className="text-slate-400 text-center max-w-md">
   107→          We'll notify you when we launch. Get ready to see how AI really thinks.
   108→        </p>
   109→      </motion.div>
   110→    );
   111→  }
   112→
   113→  return (
   114→    <div className="w-full max-w-md mx-auto">
   115→      <Form {...form}>
   116→        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
   117→          {/* Email Field */}
   118→          <FormField
   119→            control={form.control}
   120→            name="email"
   121→            render={({ field }) => (
   122→              <FormItem>
   123→                <FormLabel className="text-white font-medium">
   124→                  Email address *
   125→                </FormLabel>
   126→                <FormControl>
   127→                  <Input
   128→                    placeholder="you@company.com"
   129→                    {...field}
   130→                    className="bg-white/5 border-white/10 text-white placeholder:text-slate-500 focus:border-[hsl(var(--brand-primary))] focus:ring-[hsl(var(--brand-primary))]"
   131→                  />
   132→                </FormControl>
   133→                <FormMessage className="text-[hsl(var(--brand-accent))]" />
   134→              </FormItem>
   135→            )}
   136→          />
   137→
   138→          {/* Name Field (Optional) */}
   139→          <FormField
   140→            control={form.control}
   141→            name="name"
   142→            render={({ field }) => (
   143→              <FormItem>
   144→                <FormLabel className="text-white font-medium">
   145→                  Name <span className="text-slate-500">(optional)</span>
   146→                </FormLabel>
   147→                <FormControl>
   148→                  <Input
   149→                    placeholder="Your name"
   150→                    {...field}
   151→                    className="bg-white/5 border-white/10 text-white placeholder:text-slate-500 focus:border-[hsl(var(--brand-primary))] focus:ring-[hsl(var(--brand-primary))]"
   152→                  />
   153→                </FormControl>
   154→                <FormMessage className="text-[hsl(var(--brand-accent))]" />
   155→              </FormItem>
   156→            )}
   157→          />
   158→
   159→          {/* Privacy Checkbox */}
   160→          <FormField
   161→            control={form.control}
   162→            name="agreeToPrivacy"
   163→            render={({ field }) => (
   164→              <FormItem className="flex flex-row items-start space-x-3 space-y-0">
   165→                <FormControl>
   166→                  <Checkbox
   167→                    checked={field.value}
   168→                    onCheckedChange={field.onChange}
   169→                    className="border-white/20 data-[state=checked]:bg-[hsl(var(--brand-primary))] data-[state=checked]:border-[hsl(var(--brand-primary))]"
   170→                  />
   171→                </FormControl>
   172→                <div className="space-y-1 leading-none">
   173→                  <FormLabel className="text-sm text-slate-400 font-normal cursor-pointer">
   174→                    I agree to receive updates and understand my data will be
   175→                    processed according to the privacy policy.
   176→                  </FormLabel>
   177→                  <FormMessage className="text-[hsl(var(--brand-accent))]" />
   178→                </div>
   179→              </FormItem>
   180→            )}
   181→          />
   182→
   183→          {/* Root Error */}
   184→          {form.formState.errors.root && (
   185→            <p className="text-sm text-[hsl(var(--brand-accent))]">
   186→              {form.formState.errors.root.message}
   187→            </p>
   188→          )}
   189→
   190→          {/* Submit Button */}
   191→          <Button
   192→            type="submit"
   193→            disabled={isSubmitting}
   194→            className="w-full bg-[hsl(var(--brand-primary))] hover:bg-[hsl(var(--brand-primary)/0.85)] text-white font-semibold py-6 rounded-lg shadow-[0_10px_25px_hsl(var(--brand-primary)/0.4)] hover:shadow-[0_15px_35px_hsl(var(--brand-primary)/0.5)] transition-all duration-300"
   195→          >
   196→            {isSubmitting ? (
   197→              <>
   198→                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
   199→                Joining...
   200→              </>
   201→            ) : (
   202→              "Join the Waitlist"
   203→            )}
   204→          </Button>
   205→        </form>
   206→      </Form>
   207→
   208→      {/* Trust indicators */}
   209→      {count > 0 && (
   210→        <p className="mt-6 text-xs text-center text-slate-500">
   211→          Join <span className="text-[hsl(var(--brand-primary))] font-semibold">{count.toLocaleString()}+</span> people
   212→          already on the waitlist
   213→        </p>
   214→      )}
   215→    </div>
   216→  );
   217→}
   218→
   219→export default WaitlistForm;
   220→

