     1→import { MentalModelData } from "../models/interfaces.js";
     2→import chalk from "chalk";
     3→
     4→export class ModelServer {
     5→    private validateModelData(input: unknown): MentalModelData {
     6→        const data = input as Record<string, unknown>;
     7→
     8→        if (!data.modelName || typeof data.modelName !== "string") {
     9→            throw new Error("Invalid modelName: must be a string");
    10→        }
    11→        if (!data.problem || typeof data.problem !== "string") {
    12→            throw new Error("Invalid problem: must be a string");
    13→        }
    14→
    15→        return {
    16→            modelName: data.modelName as string,
    17→            problem: data.problem as string,
    18→            steps: Array.isArray(data.steps) ? data.steps.map(String) : [],
    19→            reasoning:
    20→                typeof data.reasoning === "string"
    21→                    ? (data.reasoning as string)
    22→                    : "",
    23→            conclusion:
    24→                typeof data.conclusion === "string"
    25→                    ? (data.conclusion as string)
    26→                    : "",
    27→        };
    28→    }
    29→
    30→    private formatModelOutput(data: MentalModelData): string {
    31→        const { modelName, problem, steps, reasoning, conclusion } = data;
    32→
    33→        let output = `\n${chalk.bold.blue("Mental Model:")} ${chalk.bold(
    34→            modelName
    35→        )}\n`;
    36→        output += `${chalk.bold.green("Problem:")} ${problem}\n`;
    37→
    38→        if (steps.length > 0) {
    39→            output += `\n${chalk.bold.yellow("Steps:")}\n`;
    40→            steps.forEach((step, index) => {
    41→                output += `${chalk.bold(`${index + 1}.`)} ${step}\n`;
    42→            });
    43→        }
    44→
    45→        if (reasoning) {
    46→            output += `\n${chalk.bold.magenta("Reasoning:")}\n${reasoning}\n`;
    47→        }
    48→
    49→        if (conclusion) {
    50→            output += `\n${chalk.bold.cyan("Conclusion:")}\n${conclusion}\n`;
    51→        }
    52→
    53→        return output;
    54→    }
    55→
    56→    public processModel(input: unknown): any {
    57→        try {
    58→            const validatedInput = this.validateModelData(input);
    59→            const formattedOutput = this.formatModelOutput(validatedInput);
    60→            console.error(formattedOutput);
    61→
    62→            return {
    63→                modelName: validatedInput.modelName,
    64→                status: "success",
    65→                hasSteps: validatedInput.steps.length > 0,
    66→                hasConclusion: !!validatedInput.conclusion,
    67→            };
    68→        } catch (error) {
    69→            return {
    70→                error: error instanceof Error ? error.message : String(error),
    71→                status: "failed",
    72→            };
    73→        }
    74→    }
    75→}
