    82→
    83→    // Score all personas based on topic relevance
    84→    for (const persona of allPersonas) {
    85→        let score = 0;
    86→
    87→        // Check name match (highest weight)
    88→        if (persona.name.toLowerCase().includes(topicLower)) {
    89→            score += 5;
    90→        }
    91→
    92→        // Check expertise match (high weight)
    93→        for (const exp of persona.expertise) {
    94→            if (exp.toLowerCase().includes(topicLower)) {
    95→                score += 3;
    96→            }
    97→        }
    98→
    99→        // Check tag match (medium weight)
   100→        for (const tag of persona.tags) {
   101→            if (tag.toLowerCase().includes(topicLower)) {
   102→                score += 2;
   103→            }
   104→        }
   105→
   106→        // Check concerns match (low weight)
   107→        for (const concern of persona.concerns) {
   108→            if (concern.toLowerCase().includes(topicLower)) {
   109→                score += 1;
   110→            }
   111→        }
   112→
   113→        // Check typical questions match (low weight)
   114→        for (const question of persona.typicalQuestions) {
   115→            if (question.toLowerCase().includes(topicLower)) {
   116→                score += 1;
   117→            }
   118→        }
   119→
   120→        if (score > 0) {
   121→            suggestions.push({ persona, score, category: persona.category });
   122→        }
   123→    }
   124→
   125→    // Sort by score (descending)
   126→    suggestions.sort((a, b) => b.score - a.score);
   127→
   128→    // Select personas ensuring diversity across categories
   129→    const selected: PredefinedPersona[] = [];
   130→    const categoryCounts = new Map<PersonaCategoryId, number>();
   131→
   132→    for (const suggestion of suggestions) {
   133→        if (selected.length >= maxPersonas) {
   134→            break;
   135→        }
   136→
   137→        const categoryCount = categoryCounts.get(suggestion.category) || 0;
   138→
   139→        // Prefer diversity: limit to 2 personas per category unless we need more
   140→        if (categoryCount < 2 || selected.length >= maxPersonas - 1) {
   141→            selected.push(suggestion.persona);
   142→            categoryCounts.set(suggestion.category, categoryCount + 1);
   143→        }
   144→    }
   145→
   146→    // If we didn't get enough diverse personas, add more from the top-scored ones
   147→    if (selected.length < maxPersonas) {
   148→        for (const suggestion of suggestions) {
   149→            if (selected.length >= maxPersonas) {
