   181→            if (compPersona) {
   182→                complementary.push(compPersona);
   183→            }
   184→        }
   185→    }
   186→
   187→    // If we need more, find personas from different categories with overlapping concerns
   188→    if (complementary.length < maxResults) {
   189→        const otherPersonas = allPersonas.filter(
   190→            p => p.id !== personaId &&
   191→            p.category !== persona.category &&
   192→            !complementary.find(comp => comp.id === p.id)
   193→        );
   194→
   195→        const scored = otherPersonas.map(p => ({
   196→            persona: p,
   197→            score: p.concerns.filter(c => persona.concerns.includes(c)).length
   198→        }));
   199→
   200→        scored.sort((a, b) => b.score - a.score);
   201→
   202→        for (const item of scored) {
   203→            if (complementary.length >= maxResults) {
   204→                break;
   205→            }
   206→            complementary.push(item.persona);
   207→        }
   208→    }
   209→
   210→    return complementary.slice(0, maxResults);
