   120→  private formatOutput(data: CollaborativeReasoningData): string {
   121→    const { topic, personas, contributions, stage, activePersonaId, iteration } = data;
   122→    
   123→    let output = `\n${chalk.bold.blue('Collaborative Reasoning Session')}\n`;
   124→    output += `${chalk.bold.green('Topic:')} ${topic}\n`;
   125→    output += `${chalk.bold.yellow('Stage:')} ${stage} (Iteration: ${iteration})\n`;
   126→    
   127→    // Active persona
   128→    const activePersona = personas.find(p => p.id === activePersonaId);
   129→    if (activePersona) {
   130→      output += `\n${chalk.bold.magenta('Active Persona:')} ${activePersona.name}\n`;
   131→      output += `${chalk.bold.cyan('Expertise:')} ${activePersona.expertise.join(', ')}\n`;
   132→      output += `${chalk.bold.cyan('Perspective:')} ${activePersona.perspective}\n`;
   133→    }
   134→    
   135→    // Contributions
   136→    if (contributions.length > 0) {
   137→      output += `\n${chalk.bold.green('Contributions:')}\n`;
   138→      
   139→      for (const contribution of contributions) {
   140→        const persona = personas.find(p => p.id === contribution.personaId);
   141→        const personaName = persona ? persona.name : contribution.personaId;
   142→        
   143→        output += `${chalk.bold(`${personaName} (${contribution.type}, confidence: ${contribution.confidence.toFixed(2)}):`)} `;
   144→        output += `${contribution.content}\n\n`;
   145→      }
   146→    }
   147→    
   148→    // Consensus points
   149→    if (data.consensusPoints && data.consensusPoints.length > 0) {
   150→      output += `\n${chalk.bold.green('Consensus Points:')}\n`;
   151→      data.consensusPoints.forEach((point, i) => {
   152→        output += `${chalk.bold(`${i+1}.`)} ${point}\n`;
   153→      });
   154→    }
   155→    
   156→    // Key insights
   157→    if (data.keyInsights && data.keyInsights.length > 0) {
   158→      output += `\n${chalk.bold.yellow('Key Insights:')}\n`;
   159→      data.keyInsights.forEach((insight, i) => {
   160→        output += `${chalk.bold(`${i+1}.`)} ${insight}\n`;
   161→      });
   162→    }
   163→    
   164→    // Final recommendation
   165→    if (data.finalRecommendation) {
   166→      output += `\n${chalk.bold.cyan('Final Recommendation:')}\n${data.finalRecommendation}\n`;
   167→    }
   168→    
   169→    return output;
   170→  }
