     1→import { CollaborativeReasoningData } from '../models/interfaces.js';
     2→import chalk from 'chalk';
     3→
     4→export class CouncilServer {
     5→  private validateInputData(input: unknown): CollaborativeReasoningData {
     6→    const data = input as CollaborativeReasoningData;
     7→    if (!data.topic || !data.personas || !data.contributions || !data.stage || !data.activePersonaId || !data.sessionId) {
     8→      throw new Error("Invalid input for CollaborativeReasoning: Missing required fields.");
     9→    }
    10→    if (typeof data.iteration !== 'number' || data.iteration < 0) {
    11→        throw new Error("Invalid iteration value for CollaborativeReasoningData.");
    12→    }
    13→    if (typeof data.nextContributionNeeded !== 'boolean') {
    14→        throw new Error("Invalid nextContributionNeeded value for CollaborativeReasoningData.");
    15→    }
    16→    return data;
    17→  }
    18→
    19→  private formatOutput(data: CollaborativeReasoningData): string {
    20→    const { topic, personas, contributions, stage, activePersonaId, iteration } = data;
    21→    
    22→    let output = `\n${chalk.bold.blue('Collaborative Reasoning Session')}\n`;
    23→    output += `${chalk.bold.green('Topic:')} ${topic}\n`;
    24→    output += `${chalk.bold.yellow('Stage:')} ${stage} (Iteration: ${iteration})\n`;
    25→    
    26→    // Active persona
    27→    const activePersona = personas.find(p => p.id === activePersonaId);
    28→    if (activePersona) {
    29→      output += `\n${chalk.bold.magenta('Active Persona:')} ${activePersona.name}\n`;
    30→      output += `${chalk.bold.cyan('Expertise:')} ${activePersona.expertise.join(', ')}\n`;
    31→      output += `${chalk.bold.cyan('Perspective:')} ${activePersona.perspective}\n`;
    32→    }
    33→    
    34→    // Contributions
    35→    if (contributions.length > 0) {
    36→      output += `\n${chalk.bold.green('Contributions:')}\n`;
    37→      
    38→      for (const contribution of contributions) {
    39→        const persona = personas.find(p => p.id === contribution.personaId);
    40→        const personaName = persona ? persona.name : contribution.personaId;
    41→        
    42→        output += `${chalk.bold(`${personaName} (${contribution.type}, confidence: ${contribution.confidence.toFixed(2)}):`)} `;
    43→        output += `${contribution.content}\n\n`;
    44→      }
    45→    }
    46→    
    47→    // Consensus points
    48→    if (data.consensusPoints && data.consensusPoints.length > 0) {
    49→      output += `\n${chalk.bold.green('Consensus Points:')}\n`;
    50→      data.consensusPoints.forEach((point, i) => {
    51→        output += `${chalk.bold(`${i+1}.`)} ${point}\n`;
    52→      });
    53→    }
    54→    
    55→    // Key insights
    56→    if (data.keyInsights && data.keyInsights.length > 0) {
    57→      output += `\n${chalk.bold.yellow('Key Insights:')}\n`;
    58→      data.keyInsights.forEach((insight, i) => {
    59→        output += `${chalk.bold(`${i+1}.`)} ${insight}\n`;
    60→      });
    61→    }
    62→    
    63→    // Final recommendation
    64→    if (data.finalRecommendation) {
    65→      output += `\n${chalk.bold.cyan('Final Recommendation:')}\n${data.finalRecommendation}\n`;
    66→    }
    67→    
    68→    return output;
    69→  }
    70→
    71→  public processCollaborativeReasoning(input: unknown): CollaborativeReasoningData {
    72→    const validatedData = this.validateInputData(input);
    73→    
    74→    // Log formatted output to console
    75→    const formattedOutput = this.formatOutput(validatedData);
    76→    console.error(formattedOutput);
    77→    
    78→    return validatedData;
    79→  }
    80→}
    81→

