    13→/**
    14→ * Central registry for managing predefined personas
    15→ * Provides storage, retrieval, and query capabilities for personas
    16→ */
    17→export class PersonaRegistry {
    18→    private personas: Map<string, PredefinedPersona>;
    19→    private categories: Map<PersonaCategoryId, PersonaCategory>;
    20→    private personasByCategory: Map<PersonaCategoryId, PredefinedPersona[]>;
    21→    private version: string;
    22→    private lastUpdated: string;
    23→
    24→    constructor() {
    25→        this.personas = new Map();
    26→        this.categories = new Map();
    27→        this.personasByCategory = new Map();
    28→        this.version = '1.0.0';
    29→        this.lastUpdated = new Date().toISOString();
    30→    }
    31→
    32→    /**
    33→     * Register a category in the registry
    34→     */
    35→    public registerCategory(category: PersonaCategory): void {
    36→        this.categories.set(category.id, category);
    37→        if (!this.personasByCategory.has(category.id)) {
    38→            this.personasByCategory.set(category.id, []);
    39→        }
    40→    }
    41→
    42→    /**
    43→     * Register a predefined persona in the registry
    44→     */
    45→    public registerPersona(persona: PredefinedPersona): void {
    46→        if (!persona.id) {
    47→            throw new Error('Persona must have an id');
    48→        }
    49→        if (!persona.category) {
    50→            throw new Error('Persona must have a category');
    51→        }
    52→
    53→        this.personas.set(persona.id, persona);
    54→
    55→        // Add to category index
    56→        if (!this.personasByCategory.has(persona.category)) {
    57→            this.personasByCategory.set(persona.category, []);
    58→        }
    59→        const categoryPersonas = this.personasByCategory.get(persona.category)!;
    60→        if (!categoryPersonas.find(p => p.id === persona.id)) {
    61→            categoryPersonas.push(persona);
    62→        }
    63→
    64→        this.lastUpdated = new Date().toISOString();
    65→    }
    66→
    67→    /**
    68→     * Register multiple personas at once
    69→     */
    70→    public registerPersonas(personas: PredefinedPersona[]): void {
    71→        for (const persona of personas) {
    72→            this.registerPersona(persona);
    73→        }
    74→    }
    75→
    76→    /**
    77→     * Get a persona by its ID
    78→     */
    79→    public getPersonaById(id: string): PredefinedPersona | undefined {
    80→        return this.personas.get(id);
    81→    }
    82→
    83→    /**
    84→     * Get all personas in a specific category
    85→     */
    86→    public getPersonasByCategory(categoryId: PersonaCategoryId): PredefinedPersona[] {
    87→        return this.personasByCategory.get(categoryId) || [];
    88→    }
    89→
    90→    /**
    91→     * Get all registered personas
    92→     */
    93→    public getAllPersonas(): PredefinedPersona[] {
    94→        return Array.from(this.personas.values());
