   109→    }
   110→
   111→    /**
   112→     * Query personas based on various filters
   113→     */
   114→    public queryPersonas(options: PersonaQueryOptions): PredefinedPersona[] {
   115→        let results = this.getAllPersonas();
   116→
   117→        // Filter by category
   118→        if (options.category) {
   119→            results = results.filter(p => p.category === options.category);
   120→        }
   121→
   122→        // Filter by expertise
   123→        if (options.expertise && options.expertise.length > 0) {
   124→            results = results.filter(p =>
   125→                options.expertise!.some(exp =>
   126→                    p.expertise.some(pExp =>
   127→                        pExp.toLowerCase().includes(exp.toLowerCase())
   128→                    )
   129→                )
   130→            );
   131→        }
   132→
   133→        // Filter by tags
   134→        if (options.tags && options.tags.length > 0) {
   135→            results = results.filter(p =>
   136→                options.tags!.some(tag =>
   137→                    p.tags.some(pTag =>
   138→                        pTag.toLowerCase().includes(tag.toLowerCase())
   139→                    )
   140→                )
   141→            );
   142→        }
   143→
   144→        // Filter by keywords (search in name, background, perspective)
   145→        if (options.keywords && options.keywords.length > 0) {
   146→            results = results.filter(p => {
   147→                const searchText = `${p.name} ${p.background} ${p.perspective}`.toLowerCase();
   148→                return options.keywords!.some(keyword =>
   149→                    searchText.includes(keyword.toLowerCase())
   150→                );
   151→            });
   152→        }
   153→
   154→        // Apply limit
   155→        if (options.limit && options.limit > 0) {
   156→            results = results.slice(0, options.limit);
   157→        }
   158→
   159→        return results;
   160→    }
   161→
   162→    /**
   163→     * Search personas and return results with relevance scores
   164→     */
   165→    public searchPersonas(options: PersonaQueryOptions): PersonaSearchResult[] {
   166→        const personas = this.queryPersonas(options);
   167→
   168→        return personas.map(persona => {
   169→            let relevance = 0;
   170→            const matchReasons: string[] = [];
   171→
   172→            // Calculate relevance score based on matches
   173→            if (options.category && persona.category === options.category) {
   174→                relevance += 0.3;
   175→                matchReasons.push(`in ${options.category} category`);
   176→            }
   177→
   178→            if (options.expertise && options.expertise.length > 0) {
   179→                const matchingExpertise = options.expertise.filter(exp =>
   180→                    persona.expertise.some(pExp =>
   181→                        pExp.toLowerCase().includes(exp.toLowerCase())
   182→                    )
   183→                );
   184→                if (matchingExpertise.length > 0) {
   185→                    relevance += 0.3 * (matchingExpertise.length / options.expertise.length);
   186→                    matchReasons.push(`expertise in ${matchingExpertise.join(', ')}`);
   187→                }
   188→            }
   189→
   190→            if (options.tags && options.tags.length > 0) {
   191→                const matchingTags = options.tags.filter(tag =>
   192→                    persona.tags.some(pTag =>
   193→                        pTag.toLowerCase().includes(tag.toLowerCase())
   194→                    )
   195→                );
   196→                if (matchingTags.length > 0) {
   197→                    relevance += 0.2 * (matchingTags.length / options.tags.length);
   198→                    matchReasons.push(`tagged as ${matchingTags.join(', ')}`);
   199→                }
   200→            }
   201→
   202→            if (options.keywords && options.keywords.length > 0) {
   203→                const searchText = `${persona.name} ${persona.background} ${persona.perspective}`.toLowerCase();
   204→                const matchingKeywords = options.keywords.filter(keyword =>
   205→                    searchText.includes(keyword.toLowerCase())
   206→                );
   207→                if (matchingKeywords.length > 0) {
   208→                    relevance += 0.2 * (matchingKeywords.length / options.keywords.length);
   209→                    matchReasons.push(`matches keywords: ${matchingKeywords.join(', ')}`);
   210→                }
   211→            }
   212→
   213→            // Default relevance if no specific matches
   214→            if (relevance === 0) {
   215→                relevance = 0.1;
   216→                matchReasons.push('general match');
   217→            }
   218→
   219→            return {
   220→                persona,
   221→                relevance: Math.min(relevance, 1.0),
   222→                matchReason: matchReasons.join('; ')
   223→            };
   224→        }).sort((a, b) => b.relevance - a.relevance);
   225→    }
   226→
   227→    /**
   228→     * Get personas by expertise areas
   229→     */
   230→    public getPersonasByExpertise(expertise: string[]): PredefinedPersona[] {
   231→        return this.queryPersonas({ expertise });
   232→    }
   233→
   234→    /**
   235→     * Get personas by tags
   236→     */
   237→    public getPersonasByTags(tags: string[]): PredefinedPersona[] {
   238→        return this.queryPersonas({ tags });
   239→    }
   240→
   241→    /**
   242→     * Search personas by keywords
   243→     */
   244→    public searchByKeywords(keywords: string[]): PredefinedPersona[] {
   245→        return this.queryPersonas({ keywords });
   246→    }
