   117→  public processVisualReasoning(input: unknown): { content: Array<{ type: string; text: string }>; isError?: boolean } {
   118→    try {
   119→      const validatedData = this.validateInputData(input);
   120→      const processedData: VisualOperationData = {
   121→        ...validatedData,
   122→        elements: validatedData.elements || []
   123→      };
   124→
   125→      // Generate Mermaid output if elements are provided
   126→      let mermaidOutput: string | undefined;
   127→      if (processedData.elements && processedData.elements.length > 0) {
   128→        const mermaidGenerator = new MermaidGenerator();
   129→        mermaidOutput = mermaidGenerator.generate(processedData.diagramType, processedData.elements);
   130→        processedData.mermaidOutput = mermaidOutput;
   131→      }
   132→
   133→      const formattedOutput = this.formatOutput(processedData);
   134→      console.error(formattedOutput);
   135→
   136→      return {
   137→        content: [{
   138→          type: "text",
   139→          text: JSON.stringify({
   140→            operation: processedData.operation,
   141→            diagramId: processedData.diagramId,
   142→            diagramType: processedData.diagramType,
   143→            iteration: processedData.iteration,
   144→            nextOperationNeeded: processedData.nextOperationNeeded,
   145→            elementCount: processedData.elements ? processedData.elements.length : 0,
   146→            mermaidOutput: mermaidOutput,
   147→            status: 'success'
   148→          }, null, 2)
   149→        }]
   150→      };
   151→    } catch (error) {
   152→      return {
   153→        content: [{
   154→          type: "text",
   155→          text: JSON.stringify({
   156→            error: error instanceof Error ? error.message : String(error),
   157→            status: 'failed'
   158→          }, null, 2)
   159→        }],
   160→        isError: true
   161→      };
   162→    }
   163→  }
