     0→import { VisualElement, VisualOperationData } from '../models/interfaces.js';
     1→
     2→export class MermaidGenerator {
     3→  /**
     4→   * Generate Mermaid syntax from VisualElement array based on diagram type
     5→   */
     6→  public generate(diagramType: VisualOperationData['diagramType'], elements: VisualElement[]): string {
     7→    if (!elements || elements.length === 0) {
     8→      return '';
     9→    }
    10→
    11→    switch (diagramType) {
    12→      case 'sequenceDiagram':
    13→        return this.generateSequenceDiagram(elements);
    14→      case 'stateMachine':
    15→        return this.generateStateMachine(elements);
    16→      case 'erDiagram':
    17→        return this.generateERDiagram(elements);
    18→      case 'mindMap':
    19→        return this.generateMindMap(elements);
    20→      case 'contextDiagram':
    21→        return this.generateContextDiagram(elements);
    22→      case 'graph':
    23→        return this.generateGraph(elements, 'TD');
    24→      case 'flowchart':
    25→        return this.generateFlowchart(elements);
    26→      case 'stateDiagram':
    27→        return this.generateStateDiagram(elements);
    28→      case 'conceptMap':
    29→        return this.generateConceptMap(elements);
    30→      case 'treeDiagram':
    31→        return this.generateTreeDiagram(elements);
    32→      case 'custom':
    33→        return this.generateCustom(elements);
    34→      default:
    35→        return '';
    36→    }
    37→  }
    38→
    39→  /**
    40→   * Generate Mermaid sequenceDiagram syntax
    41→   * Nodes become participants, edges become messages with arrow notation
    42→   */
    43→  private generateSequenceDiagram(elements: VisualElement[]): string {
    44→    const lines: string[] = ['sequenceDiagram'];
    45→
    46→    const nodes = elements.filter(e => e.type === 'node');
    47→    const edges = elements.filter(e => e.type === 'edge');
    48→
    49→    // Add participants
    50→    nodes.forEach(node => {
    51→      const participantName = this.sanitizeId(node.id);
    52→      const displayName = node.label || node.id;
    53→      lines.push(`    participant ${participantName} as ${displayName}`);
    54→    });
    55→
    56→    // Add messages (edges)
    57→    edges.forEach(edge => {
    58→      if (edge.source && edge.target) {
    59→        const from = this.sanitizeId(edge.source);
    60→        const to = this.sanitizeId(edge.target);
    61→        const label = edge.label || '';
    62→        const arrowType = edge.properties?.arrowType || '->>';
    63→        lines.push(`    ${from}${arrowType}${to}: ${label}`);
    64→      }
    65→    });
    66→
    67→    return lines.join('\n');
    68→  }
    69→
    70→  /**
    71→   * Generate Mermaid stateDiagram-v2 syntax for state machines
    72→   * Nodes become states, edges become transitions
    73→   */
    74→  private generateStateMachine(elements: VisualElement[]): string {
    75→    const lines: string[] = ['stateDiagram-v2'];
    76→
    77→    const nodes = elements.filter(e => e.type === 'node');
    78→    const edges = elements.filter(e => e.type === 'edge');
    79→
    80→    // Add states
    81→    nodes.forEach(node => {
    82→      const stateName = this.sanitizeId(node.id);
    83→      if (node.properties?.isStart) {
    84→        lines.push(`    [*] --> ${stateName}`);
    85→      }
    86→      if (node.label && node.label !== node.id) {
    87→        lines.push(`    ${stateName}: ${node.label}`);
    88→      }
    89→      if (node.properties?.isEnd) {
    90→        lines.push(`    ${stateName} --> [*]`);
    91→      }
    92→    });
    93→
    94→    // Add transitions
    95→    edges.forEach(edge => {
    96→      if (edge.source && edge.target) {
    97→        const from = this.sanitizeId(edge.source);
    98→        const to = this.sanitizeId(edge.target);
    99→        const label = edge.label ? `: ${edge.label}` : '';
   100→        lines.push(`    ${from} --> ${to}${label}`);
   101→      }
   102→    });
   103→
   104→    return lines.join('\n');
   105→  }
   106→
   107→  /**
   108→   * Generate Mermaid erDiagram syntax
   109→   * Nodes become entities with attributes, edges become relationships
   110→   */
   111→  private generateERDiagram(elements: VisualElement[]): string {
   112→    const lines: string[] = ['erDiagram'];
   113→
   114→    const nodes = elements.filter(e => e.type === 'node');
   115→    const edges = elements.filter(e => e.type === 'edge');
   116→
   117→    // Add entities with attributes
   118→    nodes.forEach(node => {
   119→      const entityName = this.sanitizeId(node.id);
   120→      lines.push(`    ${entityName} {`);
   121→
   122→      const attributes = node.properties?.attributes;
   123→      if (attributes && Array.isArray(attributes)) {
   124→        attributes.forEach((attr: any) => {
   125→          const attrType = attr.type || 'string';
   126→          const attrName = attr.name || attr;
   127→          const key = attr.key ? ' PK' : '';
   128→          lines.push(`        ${attrType} ${attrName}${key}`);
   129→        });
   130→      }
   131→
   132→      lines.push('    }');
   133→    });
   134→
   135→    // Add relationships
   136→    edges.forEach(edge => {
   137→      if (edge.source && edge.target) {
   138→        const from = this.sanitizeId(edge.source);
   139→        const to = this.sanitizeId(edge.target);
   140→        const cardinality = edge.properties?.cardinality || '||--o{';
   141→        const label = edge.label ? ` : "${edge.label}"` : '';
   142→        lines.push(`    ${from} ${cardinality} ${to}${label}`);
   143→      }
   144→    });
   145→
   146→    return lines.join('\n');
