   150→  /**
   151→   * Generate Mermaid mindmap syntax
   152→   * Root node at center, child nodes branch out via contains relationships
   153→   */
   154→  private generateMindMap(elements: VisualElement[]): string {
   155→    const lines: string[] = ['mindmap'];
   156→
   157→    const nodes = elements.filter(e => e.type === 'node');
   158→
   159→    // Find root node (node with no incoming edges or marked as root)
   160→    const rootNode = nodes.find(n => n.properties?.isRoot) || nodes[0];
   161→
   162→    if (!rootNode) {
   163→      return 'mindmap\n  root((Root))';
   164→    }
   165→
   166→    const processedNodes = new Set<string>();
   167→
   168→    const addNode = (node: VisualElement, indent: number) => {
   169→      if (processedNodes.has(node.id)) return;
   170→      processedNodes.add(node.id);
   171→
   172→      const indentStr = '  '.repeat(indent);
   173→      const label = node.label || node.id;
   174→
   175→      // Determine shape based on properties
   176→      let nodeText = label;
   177→      if (node.properties?.shape === 'circle') {
   178→        nodeText = `((${label}))`;
   179→      } else if (node.properties?.shape === 'square') {
   180→        nodeText = `[${label}]`;
   181→      } else if (node.properties?.shape === 'cloud') {
   182→        nodeText = `)${label}(`;
   183→      }
   184→
   185→      lines.push(`${indentStr}${nodeText}`);
   186→
   187→      // Process children
   188→      if (node.contains && node.contains.length > 0) {
   189→        node.contains.forEach(childId => {
   190→          const childNode = nodes.find(n => n.id === childId);
   191→          if (childNode) {
   192→            addNode(childNode, indent + 1);
   193→          }
   194→        });
   195→      }
   196→    };
   197→
   198→    addNode(rootNode, 1);
   199→
   200→    return lines.join('\n');
   201→  }
