   208→    const lines: string[] = ['C4Context'];
   209→    lines.push('    title System Context Diagram');
   210→    lines.push('');
   211→
   212→    const nodes = elements.filter(e => e.type === 'node');
   213→    const edges = elements.filter(e => e.type === 'edge');
   214→
   215→    // Add nodes
   216→    nodes.forEach(node => {
   217→      const id = this.sanitizeId(node.id);
   218→      const label = node.label || node.id;
   219→      const description = node.properties?.description || '';
   220→      const nodeType = node.properties?.nodeType || 'System';
   221→
   222→      switch (nodeType) {
   223→        case 'Person':
   224→          lines.push(`    Person(${id}, "${label}", "${description}")`);
   225→          break;
   226→        case 'SystemDb':
