     1→import { VisualOperationData } from '../models/interfaces.js';
     2→import chalk from 'chalk';
     3→import { MermaidGenerator } from './mermaidGenerator.js';
     4→
     5→export class MapServer {
     6→  private validateInputData(input: unknown): VisualOperationData {
     7→    const data = input as VisualOperationData;
     8→    if (!data.operation || !data.diagramId || !data.diagramType) {
     9→      throw new Error("Invalid input for VisualReasoning: Missing required fields.");
    10→    }
    11→    if (typeof data.iteration !== 'number' || data.iteration < 0) {
    12→      throw new Error("Invalid iteration value for VisualOperationData.");
    13→    }
    14→    if (typeof data.nextOperationNeeded !== 'boolean') {
    15→      throw new Error("Invalid nextOperationNeeded value for VisualOperationData.");
    16→    }
    17→    return data;
    18→  }
    19→
    20→  private formatOutput(data: VisualOperationData): string {
    21→    const { operation, diagramId, diagramType, iteration, nextOperationNeeded } = data;
    22→
    23→    // Visual indicators for diagram types
    24→    const diagramIcons: Record<string, string> = {
    25→      sequenceDiagram: '🔄',
    26→      stateMachine: '⚙️',
    27→      erDiagram: '🗃️',
    28→      mindMap: '🧠',
    29→      contextDiagram: '🏛️',
    30→      graph: '📊',
    31→      flowchart: '➡️',
    32→      stateDiagram: '🔀',
    33→      conceptMap: '💡',
    34→      treeDiagram: '🌳',
    35→      custom: '✨'
    36→    };
    37→
    38→    const diagramIcon = diagramIcons[diagramType] || '📐';
    39→
    40→    let output = `\n${chalk.bold.blue('Visual Reasoning')}\n`;
    41→    output += `${chalk.bold.green('Operation:')} ${operation}\n`;
    42→    output += `${chalk.bold.yellow('Diagram ID:')} ${diagramId}\n`;
    43→    output += `${chalk.bold.magenta('Diagram Type:')} ${diagramIcon}  ${diagramType}\n`;
    44→    output += `${chalk.bold.cyan('Iteration:')} ${iteration}\n`;
    45→    
    46→    // Elements
    47→    if (data.elements && data.elements.length > 0) {
    48→      output += `\n${chalk.bold.green('Elements:')}\n`;
    49→      data.elements.forEach((element, i) => {
    50→        output += `${chalk.bold(`Element ${i+1}: ${element.id} (${element.type})`)}\n`;
    51→        if (element.label) {
    52→          output += `  ${chalk.bold('Label:')} ${element.label}\n`;
    53→        }
    54→        
    55→        if (element.source) {
    56→          output += `  ${chalk.bold('Source:')} ${element.source}\n`;
    57→        }
    58→        
    59→        if (element.target) {
    60→          output += `  ${chalk.bold('Target:')} ${element.target}\n`;
    61→        }
    62→        
    63→        if (element.contains && element.contains.length > 0) {
    64→          output += `  ${chalk.bold('Contains:')} ${element.contains.join(', ')}\n`;
    65→        }
    66→        
    67→        const propKeys = Object.keys(element.properties);
    68→        if (propKeys.length > 0) {
    69→          output += `  ${chalk.bold('Properties:')}\n`;
    70→          propKeys.forEach(key => {
    71→            output += `    ${chalk.bold(`${key}:`)} ${JSON.stringify(element.properties[key])}\n`;
    72→          });
    73→        }
    74→      });
    75→    }
    76→
    77→    // Mermaid Preview
    78→    if (data.mermaidOutput) {
    79→      output += `\n${chalk.bold.blue('━'.repeat(60))}\n`;
    80→      output += `${chalk.bold.green('Mermaid Diagram Preview:')}\n`;
    81→      output += `${chalk.bold.blue('━'.repeat(60))}\n`;
    82→      output += `${chalk.gray(data.mermaidOutput)}\n`;
    83→      output += `${chalk.bold.blue('━'.repeat(60))}\n`;
    84→      output += `${chalk.dim('💡 Tip: Copy the above to render at https://mermaid.live or in your Markdown editor')}\n`;
    85→    }
    86→
    87→    // Transform Type
    88→    if (data.transformationType) {
    89→      output += `\n${chalk.bold('Transform Type:')} ${data.transformationType}\n`;
    90→    }
    91→    
    92→    // Observation
    93→    if (data.observation) {
    94→      output += `\n${chalk.bold.yellow('Observation:')}\n${data.observation}\n`;
    95→    }
    96→    
    97→    // Insight
    98→    if (data.insight) {
    99→      output += `\n${chalk.bold.magenta('Insight:')}\n${data.insight}\n`;
   100→    }
   101→    
   102→    // Hypothesis
   103→    if (data.hypothesis) {
   104→      output += `\n${chalk.bold.cyan('Hypothesis:')}\n${data.hypothesis}\n`;
   105→    }
   106→    
   107→    // Next Operation
   108→    if (nextOperationNeeded) {
   109→      output += `\n${chalk.green('Further visual operations needed.')}\n`;
   110→    } else {
   111→      output += `\n${chalk.cyan('Visual reasoning complete.')}\n`;
   112→    }
   113→    
   114→    return output;
   115→  }
   116→
   117→  public processVisualReasoning(input: unknown): { content: Array<{ type: string; text: string }>; isError?: boolean } {
   118→    try {
   119→      const validatedData = this.validateInputData(input);
   120→      const processedData: VisualOperationData = {
   121→        ...validatedData,
   122→        elements: validatedData.elements || []
   123→      };
   124→
   125→      // Generate Mermaid output if elements are provided
   126→      let mermaidOutput: string | undefined;
   127→      if (processedData.elements && processedData.elements.length > 0) {
   128→        const mermaidGenerator = new MermaidGenerator();
   129→        mermaidOutput = mermaidGenerator.generate(processedData.diagramType, processedData.elements);
   130→        processedData.mermaidOutput = mermaidOutput;
   131→      }
   132→
   133→      const formattedOutput = this.formatOutput(processedData);
   134→      console.error(formattedOutput);
   135→
   136→      return {
   137→        content: [{
   138→          type: "text",
   139→          text: JSON.stringify({
   140→            operation: processedData.operation,
   141→            diagramId: processedData.diagramId,
   142→            diagramType: processedData.diagramType,
   143→            iteration: processedData.iteration,
   144→            nextOperationNeeded: processedData.nextOperationNeeded,
   145→            elementCount: processedData.elements ? processedData.elements.length : 0,
   146→            mermaidOutput: mermaidOutput,
   147→            status: 'success'
   148→          }, null, 2)
   149→        }]
   150→      };

