   190→
   191→    const risks = data.riskAssessment;
   192→    const options = data.options;
   193→
   194→    let output = `\n${chalk.bold.blue('Risk Assessment Matrix')}\n`;
   195→    output += `${chalk.dim('━'.repeat(60))}\n`;
   196→
   197→    // Helper function to determine risk level and color
   198→    const getRiskLevel = (riskScore: number): { level: string; color: any; emoji: string } => {
   199→      // Risk score = probability (0-1) × impact (1-10) = max 10
   200→      if (riskScore >= 7) {
   201→        return { level: 'CRITICAL', color: chalk.red.bold, emoji: '🔴' };
   202→      } else if (riskScore >= 4) {
   203→        return { level: 'HIGH', color: chalk.red, emoji: '🟠' };
   204→      } else if (riskScore >= 2) {
   205→        return { level: 'MEDIUM', color: chalk.yellow, emoji: '🟡' };
   206→      } else {
   207→        return { level: 'LOW', color: chalk.green, emoji: '🟢' };
   208→      }
   209→    };
   210→
   211→    // Group risks by option
   212→    const risksByOption = new Map<string, RiskItem[]>();
   213→    risks.forEach(risk => {
   214→      if (!risksByOption.has(risk.optionId)) {
   215→        risksByOption.set(risk.optionId, []);
   216→      }
   217→      risksByOption.get(risk.optionId)!.push(risk);
   218→    });
   219→
   220→    // Display risks for each option
   221→    risksByOption.forEach((optionRisks, optionId) => {
   222→      const option = options.find(opt => opt.id === optionId);
   223→      if (!option) return;
   224→
   225→      output += `\n${chalk.bold.cyan(`Option: ${option.name}`)}\n`;
   226→      output += `${chalk.dim(option.description)}\n`;
   227→
   228→      // Sort risks by risk score (highest first)
   229→      const sortedRisks = [...optionRisks].sort((a, b) => b.riskScore - a.riskScore);
   230→
   231→      output += `\n${chalk.bold('Risk Items:')}\n`;
   232→      sortedRisks.forEach(risk => {
   233→        const { level, color, emoji } = getRiskLevel(risk.riskScore);
   234→
   235→        output += `\n  ${emoji} ${color(level)} ${chalk.bold('Risk:')}\n`;
   236→        output += `     ${risk.description}\n`;
   237→
   238→        const categoryLabel = risk.category ? ` [${risk.category}]` : '';
   239→        output += `     ${chalk.yellow('Probability:')} ${(risk.probability * 100).toFixed(0)}%  `;
   240→        output += `${chalk.magenta('Impact:')} ${risk.impact.toFixed(1)}/10${categoryLabel}\n`;
   241→        output += `     ${chalk.cyan('Risk Score:')} ${color(risk.riskScore.toFixed(2))}\n`;
   242→
   243→        if (risk.mitigation && risk.mitigation.length > 0) {
   244→          output += `     ${chalk.green('Mitigation Strategies:')}\n`;
   245→          risk.mitigation.forEach(strategy => {
   246→            output += `       • ${strategy}\n`;
   247→          });
   248→        }
   249→      });
   250→
   251→      // Risk summary for this option
   252→      const avgRiskScore = optionRisks.reduce((sum, r) => sum + r.riskScore, 0) / optionRisks.length;
   253→      const maxRiskScore = Math.max(...optionRisks.map(r => r.riskScore));
   254→      const criticalRisks = optionRisks.filter(r => r.riskScore >= 7).length;
   255→      const highRisks = optionRisks.filter(r => r.riskScore >= 4 && r.riskScore < 7).length;
   256→
   257→      output += `\n  ${chalk.bold.yellow('Risk Summary:')}\n`;
   258→      output += `     Total Risks: ${optionRisks.length}  `;
   259→      output += `Critical: ${criticalRisks}  High: ${highRisks}\n`;
   260→      output += `     Average Risk Score: ${avgRiskScore.toFixed(2)}  `;
   261→      output += `Maximum Risk Score: ${maxRiskScore.toFixed(2)}\n`;
   262→
   263→      output += `\n${chalk.dim('─'.repeat(60))}\n`;
   264→    });
   265→
   266→    // Overall risk matrix visualization
   267→    output += `\n${chalk.bold.blue('Risk Matrix Visualization')}\n`;
   268→    output += `${chalk.dim('Probability vs Impact')}\n\n`;
   269→    output += `  ${chalk.bold('Impact →')}\n`;
   270→    output += `  Probability ↓     Low(1-3)  Medium(4-6)  High(7-10)\n`;
   271→    output += `  ────────────────────────────────────────────────────\n`;
   272→    output += `  High (70-100%)    ${chalk.yellow('MEDIUM')}    ${chalk.red('HIGH')}      ${chalk.red.bold('CRITICAL')}\n`;
   273→    output += `  Medium (30-70%)   ${chalk.green('LOW')}       ${chalk.yellow('MEDIUM')}    ${chalk.red('HIGH')}\n`;
   274→    output += `  Low (0-30%)       ${chalk.green('LOW')}       ${chalk.green('LOW')}       ${chalk.yellow('MEDIUM')}\n`;
   275→    output += `\n`;
   276→
   277→    return output;
   278→  }
