     4→export class DecideServer {
     5→  private validateInputData(input: unknown): DecisionFrameworkData {
     6→    const data = input as DecisionFrameworkData;
     7→    if (!data.decisionStatement || !data.options || !data.analysisType || !data.stage || !data.decisionId) {
     8→      throw new Error("Invalid input for DecisionFramework: Missing required fields.");
     9→    }
    10→    if (typeof data.iteration !== 'number' || data.iteration < 0) {
    11→        throw new Error("Invalid iteration value for DecisionFrameworkData.");
    12→    }
    13→    if (typeof data.nextStageNeeded !== 'boolean') {
    14→        throw new Error("Invalid nextStageNeeded value for DecisionFrameworkData.");
    15→    }
    16→    return data;
    17→  }
    18→
    19→  private formatOutput(data: DecisionFrameworkData): string {
    20→    const { decisionStatement, options, analysisType, stage, iteration } = data;
    21→    
    22→    let output = `\n${chalk.bold.blue('Decision Framework Analysis')}\n`;
    23→    output += `${chalk.bold.green('Decision Statement:')} ${decisionStatement}\n`;
    24→    output += `${chalk.bold.yellow('Analysis Type:')} ${analysisType}\n`;
    25→    output += `${chalk.bold.magenta('Stage:')} ${stage} (Iteration: ${iteration})\n`;
    26→    
    27→    // Options
    28→    if (options.length > 0) {
    29→      output += `\n${chalk.bold.cyan('Options:')}\n`;
    30→      options.forEach((option, i) => {
    31→        output += `${chalk.bold(`Option ${i+1}: ${option.name}`)}\n`;
    32→        output += `${option.description}\n\n`;
    33→      });
    34→    }
    35→    
    36→    // Criteria
    37→    if (data.criteria && data.criteria.length > 0) {
    38→      output += `\n${chalk.bold.green('Criteria:')}\n`;
    39→      data.criteria.forEach((criterion, i) => {
    40→        output += `${chalk.bold(`${criterion.name} (weight: ${criterion.weight.toFixed(2)}):`)} `;
    41→        output += `${criterion.description}\n`;
    42→      });
    43→    }
    44→    
    45→    // Stakeholders
    46→    if (data.stakeholders && data.stakeholders.length > 0) {
    47→      output += `\n${chalk.bold.yellow('Stakeholders:')} ${data.stakeholders.join(', ')}\n`;
    48→    }
    49→    
    50→    // Constraints
    51→    if (data.constraints && data.constraints.length > 0) {
    52→      output += `\n${chalk.bold.red('Constraints:')}\n`;
    53→      data.constraints.forEach((constraint, i) => {
    54→        output += `${chalk.bold(`${i+1}.`)} ${constraint}\n`;
    55→      });
    56→    }
    57→    
    58→    // Recommendation
    59→    if (data.recommendation) {
    60→      output += `\n${chalk.bold.green('Recommendation:')}\n${data.recommendation}\n`;
    61→      
    62→      if (data.rationale) {
    63→        output += `\n${chalk.bold.cyan('Rationale:')}\n${data.rationale}\n`;
    64→      }
    65→    }
    66→    
    67→    return output;
    68→  }
    69→
    70→  public processDecisionFramework(input: unknown): DecisionFrameworkData {
    71→    const validatedData = this.validateInputData(input);
    72→    
    73→    // Log formatted output to console
    74→    const formattedOutput = this.formatOutput(validatedData);
    75→    console.error(formattedOutput);
    76→    
    77→    return validatedData;
    78→  }
    79→}
