     1→import { z } from 'zod';
     2→
     3→const envSchema = z.object({
     4→  // Upstash Redis
     5→  UPSTASH_REDIS_REST_URL: z.string().url().optional(),
     6→  UPSTASH_REDIS_REST_TOKEN: z.string().optional(),
     7→
     8→  // Arcjet
     9→  ARCJET_KEY: z.string().optional(),
    10→
    11→  // Resend
    12→  RESEND_API_KEY: z.string().optional(),
    13→  ADMIN_EMAIL: z.string().email().optional(),
    14→
    15→  // Public
    16→  NEXT_PUBLIC_APP_URL: z.string().url().optional(),
    17→  NEXT_PUBLIC_GA_MEASUREMENT_ID: z.string().optional(),
    18→});
    19→
    20→export function validateEnv() {
    21→  const parsed = envSchema.safeParse(process.env);
    22→
    23→  if (!parsed.success) {
    24→    console.error('❌ Invalid environment variables:', parsed.error.flatten().fieldErrors);
    25→    throw new Error('Invalid environment variables');
    26→  }
    27→
    28→  // Warn about missing optional but recommended variables
    29→  const env = parsed.data;
    30→  const warnings: string[] = [];
    31→
    32→  if (!env.UPSTASH_REDIS_REST_URL || !env.UPSTASH_REDIS_REST_TOKEN) {
    33→    warnings.push('Waitlist system will not work without Upstash Redis credentials');
    34→  }
    35→
    36→  if (!env.ARCJET_KEY) {
    37→    warnings.push('Email validation will not work without Arcjet API key');
    38→  }
    39→
    40→  if (!env.RESEND_API_KEY) {
    41→    warnings.push('Email sending will not work without Resend API key');
    42→  }
    43→
    44→  if (!env.NEXT_PUBLIC_GA_MEASUREMENT_ID) {
    45→    warnings.push('Analytics will not work without Google Analytics measurement ID');
    46→  }
    47→
    48→  if (warnings.length > 0) {
    49→    console.warn('⚠️  Missing optional environment variables:');
    50→    warnings.forEach(w => console.warn(`   - ${w}`));
    51→  }
    52→
    53→  return env;
    54→}
    55→
    56→// Type-safe environment variables
    57→export type Env = z.infer<typeof envSchema>;
    58→

