    10→
    11→  useEffect(() => {
    12→    const measurementId = process.env.NEXT_PUBLIC_GA_MEASUREMENT_ID;
    13→
    14→    if (!measurementId || typeof window === "undefined") return;
    15→
    16→    // Track page views
    17→    const url = pathname + searchParams.toString();
    18→    window.gtag("config", measurementId, {
    19→      page_path: url,
    20→    });
    21→  }, [pathname, searchParams]);
    22→
    23→  const measurementId = process.env.NEXT_PUBLIC_GA_MEASUREMENT_ID;
    24→
    25→  if (!measurementId) {
    26→    return null;
    27→  }
    28→
    29→  return (
    30→    <>
    31→      <Script
    32→        strategy="afterInteractive"
    33→        src={`https://www.googletagmanager.com/gtag/js?id=${measurementId}`}
    34→      />
    35→      <Script
    36→        id="google-analytics"
    37→        strategy="afterInteractive"
    38→        dangerouslySetInnerHTML={{
    39→          __html: `
    40→            window.dataLayer = window.dataLayer || [];
    41→            function gtag(){dataLayer.push(arguments);}
    42→            gtag('js', new Date());
    43→
    44→            gtag('config', '${measurementId}', {
    45→              page_path: window.location.pathname,
    46→              anonymize_ip: true,
    47→              allow_google_signals: false
    48→            });
    49→          `,
    50→        }}
    51→      />
    52→    </>
    53→  );
    54→}
    55→
    56→export function GoogleAnalytics() {
    57→  return (
    58→    <Suspense fallback={null}>
    59→      <GoogleAnalyticsContent />
    60→    </Suspense>
    61→  );
    62→}
