     1→import { headers } from 'next/headers';
     2→import type { CSPNonce } from './csp';
     3→
     4→/**
     5→ * Retrieves the CSP nonce from request headers in server components
     6→ *
     7→ * The nonce is generated by middleware.ts and stored in the 'x-nonce' header
     8→ * for server components to access. This nonce should be added to inline scripts
     9→ * and styles to satisfy Content Security Policy requirements.
    10→ *
    11→ * @returns The CSP nonce value if available, undefined otherwise
    12→ *
    13→ * @example
    14→ * ```tsx
    15→ * import { getNonce } from '@/lib/security/get-nonce';
    16→ *
    17→ * export default function Layout() {
    18→ *   const nonce = getNonce();
    19→ *
    20→ *   return (
    21→ *     <html>
    22→ *       <head>
    23→ *         <script nonce={nonce}>
    24→ *           // Inline script that needs CSP approval
    25→ *         </script>
    26→ *       </head>
    27→ *     </html>
    28→ *   );
    29→ * }
    30→ * ```
    31→ */
    32→export function getNonce(): CSPNonce | undefined {
    33→  try {
    34→    // Access the headers in server component context
    35→    const headersList = headers();
    36→
    37→    // Retrieve the nonce that was set by middleware
    38→    const nonce = headersList.get('x-nonce');
    39→
    40→    // Return the nonce if it exists, undefined otherwise
    41→    return nonce ?? undefined;
    42→  } catch (error) {
    43→    // Headers can only be accessed in server component context
    44→    // If this fails (e.g., being called in a client component or static export),
    45→    // gracefully return undefined rather than throwing
    46→    if (process.env.NODE_ENV === 'development') {
    47→      console.warn(
    48→        'getNonce: Unable to access headers. This function must be called in a server component context.',
    49→        error
    50→      );
    51→    }
    52→    return undefined;
    53→  }
    54→}
